export async function deleteBook(id) {
  try {
    const res = await fetch(`http://localhost:8000/books/${id}`, {
      method: "DELETE",
    });

    if (!res.ok) {
      throw new Error("Failed to delete book");
    }
    return true; // success
  } catch (error) {
    console.error("Error deleting book:", error);
    return false;
  }
}
